bool CvUnit::canCoexistWithEnemyUnit(TeamTypes eTeam) const
{
/*************************************************************************************************/
/**	PeaceAndFlowers							03/27/09								Xienwolf	**/
/**																								**/
/**					Makes all Combat Actions impossible for this type of Unit					**/
/*************************************************************************************************/
	if (isNeverHostile())
	{
		return true;
	}
/*************************************************************************************************/
/**	PeaceAndFlowers							END													**/
/*************************************************************************************************/
	if (NO_TEAM == eTeam)
	{
		if(alwaysInvisible())
		{
			return true;
		}

		return false;
	}

	if(isInvisible(eTeam, false))
	{
		return true;
	}

	return false;
}

