/*************************************************************************************************/
	iStr += getStrBoost();
/*************************************************************************************************/
/**	Tweak									END													**/
/*************************************************************************************************/
	if (iStr < 0)
	{
		iStr = 0;
	}
	return iStr;
//FfH: End Add

