	{
		bHappyPre = true;
	}
	m_iCityHappy = (m_iCityHappy + iChange);
	if ((isCityHappy() || m_pUnitInfo->isMilitaryHappiness()) && !isCityNoHappy())
	{
		bHappyPost = true;
	}
	if (bHappyPre != bHappyPost)
	{
		if (plot()->isCity())
		{
			plot()->getPlotCity()->changeMilitaryHappinessUnits(bHappyPost ? 1 : -1);
		}
	}
