void CvUnit::changeDurationAlter(int iChange)
{
	if (getDuration() != 0)
	{
		changeDuration(iChange);
		if (getDuration() <= 0)
		{
			setMustDie(true);
		}
	}
}
void CvUnit::changeChangeDuration(int iChange)
{
	if (getDuration() == 0)
	{
		m_bNonTemporary = true;
	}
	changeDuration(iChange);
	if (getDuration() <= 0)
	{
		if (m_bNonTemporary)
		{
			setDuration(0);
		}
		else
		{
			setMustDie(true);
		}
	}
}
