bool CvUnit::canInfluenceWar()
{
	bool bCanInfluence = true;

	if (isNonInfluence())
	{
		bCanInfluence = false;
	}

/*************************************************************************************************/
/**	Tweak							20/10/12								Snarko				**/
/**																								**/
/**			Barbarian having influence on culture slows down the game and screw the AI			**/
/*************************************************************************************************/
	if (isBarbarian())
	{
		bCanInfluence = false;
	}
/*************************************************************************************************/
/**	Tweak								END														**/
/*************************************************************************************************/

	if (getDuration() > 0)
	{
		bCanInfluence = false;
	}
	if (isHiddenNationality())
	{
		bCanInfluence = false;
	}
	if (isAnimal())
	{
		bCanInfluence = false;
	}
	if (isInfluence())
	{
		bCanInfluence = true;
	}

	return bCanInfluence;
}

